import { Box, Image, Text, Badge, Flex, IconButton, Skeleton } from '@chakra-ui/react';
import { BiExpand } from 'react-icons/bi';
import React from 'react';
import { addToFavorites, removeFromFavorites } from '../redux/actions/productActions';
import { useDispatch, useSelector } from 'react-redux';
import { MdOutlineFavorite, MdOutlineFavoriteBorder } from 'react-icons/md';

const ProductCard = ({ product, loading }) => {
  const dispatch = useDispatch();
  const { favorites } = useSelector((state) => state.product);
  return (
    <Skeleton isLoaded={!loading} _hover={{ size: 1.5 }}>
      <Box
        _hover={{ transform: 'scale(1.1)', transitionDuration: '0.5s' }}
        borderWidth="1px"
        overflow="hidden"
        p="4"
        shadow="md"
      >
        <Image
          src={product.images[0]}
          fallbackSrc="https://via.placeholder.com/150"
          alt={product.name}
          height="200px"
        />
        {product.stock < 5 ? (
          <Badge colorScheme="yellow">Only {product.stock} Left</Badge>
        ) : product.stock < 1 ? (
          <Badge colorScheme="red">Sold Out</Badge>
        ) : (
          <Badge>In Stock</Badge>
        )}
        {product.productIsNew && (
          <Badge ml="2" colorScheme="purple">
            New
          </Badge>
        )}
        <Text noOfLines={1} fontSize="xl" mt="2" fontWeight="semibold" color="gray.600">
          {product.brand} {product.name}
        </Text>
        <Text noOfLines={1} fontSize="md" color="gray.600">
          {product.subtitle}
        </Text>

        <Flex justify="space-between" alignItems="center" mt="2">
          <Badge colorScheme="cyan">{product.category}</Badge>
          <Text fontSize="xl" fontWeight="semibold" color="cyan.600">
            ${product.price}
          </Text>
        </Flex>
        <Fle justify='space-between'>
          {favorites.includes(product._id) ? (
            <IconButton
              icon={<MdOutlineFavorite size="20px"   />}
              colorScheme='cyan'
              size="sm"
              onClick={() => dispatch(
                removeFromFavorites(product._id)
              )}
            />
          ) : (
            <IconButton
              icon={<MdOutlineFavoriteBorder size="20px"   />}
              colorScheme='cyan'
              size="sm"
              onClick={() => dispatch(
                addToFavorites(product._id)
              )}
            />
          )}

          <IconButton icon={<BiExpand size="20" />} colorScheme="cyan" size="sm"></IconButton>
        </Fle>
      </Box>
    </Skeleton>
  );
};

export default ProductCard;
